 CREATE TABLE IF NOT EXISTS `levelhst_hogast`.`param_type` (  `id` INT NOT NULL AUTO_INCREMENT,  `name` VARCHAR(45) NULL,  `number` INT NULL,  PRIMARY KEY (`id`))ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `levelhst_hogast`.`parameters` (  `id` INT NOT NULL,  `name` VARCHAR(45) NULL,  `description` VARCHAR(45) NULL,  `units` VARCHAR(45) NULL,  `exp` INT NULL,  `param_type_id` INT NOT NULL,  PRIMARY KEY (`id`, `param_type_id`),  INDEX `fk_parameters_param_type1_idx` (`param_type_id` ASC))ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `levelhst_hogast`.`periods` (  `id` INT NULL AUTO_INCREMENT,  `month` INT NULL,  `year` INT NULL,  PRIMARY KEY (`id`))ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `levelhst_hogast`.`data` (  `id` INT NOT NULL AUTO_INCREMENT,  `value` DOUBLE NULL,  `parameters_id` INT NOT NULL,  `periods_id` INT NOT NULL,  PRIMARY KEY (`id`, `parameters_id`, `periods_id`),  INDEX `fk_data_parameters_idx` (`parameters_id` ASC),  INDEX `fk_data_periods1_idx` (`periods_id` ASC))ENGINE = InnoDB;